import React from 'react'
import './main.css'
import { useState } from 'react';
const Main = () => {
    const [text, setText] = useState('');
  
    const handleChange = (event) => {
      setText(event.target.value);
    };
  
    const handleButtonClick = () => {
      console.log('Sending text:', text);
      setText('');
    };
    const [isChecked, setIsChecked] = useState(false);

  const handleToggle = () => {
    setIsChecked(!isChecked);
  };
  return (
    <div className='Home'>
        <div className="home-content">
           <h1>Where knowledge begins</h1>
           <div className='serach-box'>
            <textarea value={text} onChange={handleChange} placeholder="Ask anything..." className="textarea" />
                <div className='upload-opt'>
                    <ul>
                        <a>
                            <i className="fa-light fa-bars-filter"></i>
                            Focus
                        </a>
                        <a>
                            <i className="fa-light fa-circle-plus"></i>
                            Attach
                        </a>
                    </ul>
                    <ul>
                    <div className="toggle-switch">
      <label htmlFor="toggle" className={isChecked ? 'label-active' : 'label-inactive'}>Toggle Switch</label>
      <input
        type="checkbox"
        id="toggle"
        checked={isChecked}
        onChange={handleToggle}
      />
      <div className={isChecked ? 'switch checked' : 'switch'} onClick={handleToggle}></div>
    </div>
                        <li onClick={handleButtonClick} className={text ? 'send-btn active' : 'send-btn'} disabled={!text}>
                            Send
                        </li>
                    </ul>
                </div>
           </div>
        </div>
    </div>
  )
}

export default Main
