import React from "react";
import "./main.css";
import { useState } from "react";
const Main = () => {
    const [text, setText] = useState("");

    const handleChange = (event) => {
        setText(event.target.value);
    };

    const handleButtonClick = () => {
        console.log("Sending text:", text);
        setText("");
    };
    const autoResize = () => {
        const textarea = document.getElementById("autoResizeTextarea");
        textarea.style.height = "auto"; // Reset height to auto
        textarea.style.height = Math.min(textarea.scrollHeight, 250) + "px"; // Set height to scroll height, capped at 400px
      };
    const [isChecked, setIsChecked] = useState(false);

    const handleToggle = () => {
        setIsChecked(!isChecked);
    };

    return (
        <div className="Home">
            <div className="home-content">
                <h1>Where knowledge begins</h1>
                <div className="serach-box">
                    <textarea value={text} onChange={handleChange}  id="autoResizeTextarea" rows={1} placeholder="Ask anything..." className="textarea"   onInput={autoResize}/>
                    <div className="upload-opt">
                        <ul>
                            <a className="tooltip">
                                <i className="fa-light fa-bars-filter"></i>
                                Focus
                                <span class="tooltiptext">Set a focus for your sources</span>
                            </a>
                            <a className="tooltip">
                                <i className="fa-light fa-circle-plus"></i>
                                Attach
                                <span class="tooltiptext2">Attach images, text, or PDFs. 3 left today</span>
                            </a>
                        </ul>
                        <ul>
                            <div className="toggle-switch">
                                <input type="checkbox" id="toggle" checked={isChecked} onChange={handleToggle} />
                                <div className={isChecked ? "switch checked" : "switch"} onClick={handleToggle}></div>
                                <label htmlFor="toggle" className={isChecked ? "label-active" : "label-inactive"}>
                                    Pro
                                </label>
                            </div>
                            <li onClick={handleButtonClick} className={text ? "send-btn active" : "send-btn"} disabled={!text}>
                                Send
                            </li>
                        </ul>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Main;
